// App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import axios from 'axios';
import MovieList from './MovieList';
import MovieDetails from './MovieDetails';
import SearchBar from './SearchBar';
import Favorites from './Favorites';
import './App.css';

const App = () => {
  const [movies, setMovies] = useState([]);
  const [favorites, setFavorites] = useState([]);
  const [searchResults, setSearchResults] = useState([]);

  useEffect(() => {
    const fetchMovies = async () => {
      try {
        const response = await axios.get(
          'https://api.themoviedb.org/3/discover/movie?api_key=YOUR_API_KEY&language=en-US&sort_by=popularity.desc'
        );
        setMovies(response.data.results);
        console.log(response.data); 
      } catch (error) {
        console.error('Error fetching movies:', error);
      }
    };

    fetchMovies();
  }, []);

  const addToFavorites = (movieId) => {
    if (favorites.includes(movieId)) {
      setFavorites(favorites.filter((id) => id !== movieId));
    } else {
      setFavorites([...favorites, movieId]);
    }
  };

  const searchMovies = async (query) => {
    if (query.trim() === '') {
      setSearchResults([]);
      return;
    }

    try {
      const response = await axios.get(
        `https://api.themoviedb.org/3/search/movie?api_key=YOUR_API_KEY&language=en-US&query=${query}`
      );
      setSearchResults(response.data.results);
    } catch (error) {
      console.error('Error searching movies:', error);
    }
  };

  return (
    <Router>
      <div className="container">
        <nav>
          <ul>
            <li>
              <Link to="/">Home</Link>
            </li>
            <li>
              <Link to="/favorites">Favorites</Link>
            </li>
          </ul>
        </nav>
        <Routes>
          <Route
            path="/"
            element={
              <Home
                movies={searchResults.length > 0 ? searchResults : movies}
                addToFavorites={addToFavorites}
                favorites={favorites}
                searchMovies={searchMovies}
              />
            }
          />
          <Route path="/favorites" element={<Favorites favorites={favorites} movies={movies} />} />
          <Route path="/movie/:id" element={<MovieDetails />} />
        </Routes>
      </div>
    </Router>
  );
};

const Home = ({ movies, addToFavorites, favorites, searchMovies }) => {
  return (
    <div>
      <SearchBar onSearch={searchMovies} />
      <div className="movie-list">
        <MovieList movies={movies} addToFavorites={addToFavorites} favorites={favorites} />
      </div>
    </div>
  );
};

export default App;
