// App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import axios from 'axios';
import MovieList from './MovieList';
import MovieDetails from './MovieDetails';
import SearchBar from './SearchBar';
import Favorites from './Favorites';

const App = () => {
  const [movies, setMovies] = useState([]);
  const [favorites, setFavorites] = useState([]);
  const [searchResults, setSearchResults] = useState([]);



  useEffect(() => {
    // Fetch movies from TMDB API
    const fetchMovies = async () => {
      try {
        axios
      .request(options)
      .then(function (response) {
      console.log(response.data);
      setMovies(response.data.results);
      })
    .catch(function (error) {
      console.error(error);
    });

      } catch (error) {
        console.error('Error fetching movies:', error);
      }
    };


    fetchMovies();
  }, []);


  const addToFavorites = (movieId) => {
    if (favorites.includes(movieId)) {
      setFavorites(favorites.filter((id) => id !== movieId));
    } else {
      setFavorites([...favorites, movieId]);
    }
  };

  const searchMovies = async (query) => {
    if (query.trim() === '') {
      setSearchResults([]);
      return;
    }

    // const query = {
    //   method: 'GET',
    //   headers: {
    //     accept: 'application/json',
    //     Authorization: 'Bearer 6c0b227e894563248cdef70ac2c07278'
    //   }
    // };


    try {
      const response = await axios.get(
        `https://api.themoviedb.org/3/search/movie?api_key=6c0b227e894563248cdef70ac2c07278&language=en-US&query=${query}`
      );
      console.log(response);
      setSearchResults(response.data.results);
    } catch (error) {
      console.error('Error searching movies:', error);
    }
  };

  return (
    <Router>
      <div>
        <nav>
          <ul>
            <li>
              <Link to="/">Home</Link>
            </li>
            <li>
              <Link to="/favorites">Favorites</Link>
            </li>
          </ul>
        </nav>
        <Routes>
          <Route
            path="/"
            element={
              <Home
                movies={searchResults.length > 0 ? searchResults : movies}
                addToFavorites={addToFavorites}
                favorites={favorites}
                searchMovies={searchMovies}
              />
            }
          />
          <Route path="/favorites" element={<Favorites favorites={favorites} />} />
          <Route path="/movie/:id" element={<MovieDetails movies={movies} />} />
        </Routes>
      </div>
    </Router>
  );
};

const Home = ({ movies, addToFavorites, favorites, searchMovies }) => {
  return (
    <div>
      <SearchBar onSearch={searchMovies} />
      <Routes>
        <Route
          path="/"
          element={<MovieList movies={movies} addToFavorites={addToFavorites} favorites={favorites} />}
        />
        <Route
          path="popular"
          element={<MovieList movies={movies} addToFavorites={addToFavorites} favorites={favorites} />}
        />
      </Routes>
    </div>
  );
};

export default App;
