// App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';
import axios from 'axios';
import MovieList from './MovieList';
import MovieDetails from './MovieDetails';
import SearchBar from './SearchBar';
import Favorites from './Favorites';

const App = () => {
  const [movies, setMovies] = useState([]);
  const [favorites, setFavorites] = useState([]);
  const [searchResults, setSearchResults] = useState([]);

  useEffect(() => {
    // Fetch movies from TMDB API
    const fetchMovies = async () => {
      try {
        const response = await axios.get(
          'https://api.themoviedb.org/3/discover/movie?api_key=YOUR_API_KEY&language=en-US&sort_by=popularity.desc'
        );
        setMovies(response.data.results);
      } catch (error) {
        console.error('Error fetching movies:', error);
      }
    };

    fetchMovies();
  }, []);

  const addToFavorites = (movieId) => {
    if (favorites.includes(movieId)) {
      setFavorites(favorites.filter((id) => id !== movieId));
    } else {
      setFavorites([...favorites, movieId]);
    }
  };

  const searchMovies = async (query) => {
    if (query.trim() === '') {
      setSearchResults([]);
      return;
    }

    try {
      const response = await axios.get(
        `https://api.themoviedb.org/3/search/movie?api_key=YOUR_API_KEY&language=en-US&query=${query}`
      );
      setSearchResults(response.data.results);
    } catch (error) {
      console.error('Error searching movies:', error);
    }
  };

  return (
    <Router>
      <div>
        <nav>
          <ul>
            <li>
              <Link to="/">Home</Link>
            </li>
            <li>
              <Link to="/favorites">Favorites</Link>
            </li>
          </ul>
        </nav>
        <Switch>
          <Route path="/" exact>
            <SearchBar onSearch={searchMovies} />
            <Route path="/" exact>
              <MovieList movies={searchResults.length > 0 ? searchResults : movies} addToFavorites={addToFavorites} favorites={favorites} />
            </Route>
            <Route path="/popular" exact>
              {/* Display popular movies (same as home route) */}
              <MovieList movies={movies} addToFavorites={addToFavorites} favorites={favorites} />
            </Route>
          </Route>
          <Route path="/favorites">
            <Favorites favorites={favorites} />
          </Route>
          <Route path="/movie/:id" render={({ match }) => <MovieDetails movie={movies.find((movie) => movie.id.toString() === match.params.id)} />} />
        </Switch>
      </div>
    </Router>
  );
};

export default App;
